import React, { useState } from 'react';
import axios from 'axios';

const ResumeComponent = () => {
  const [resumeDetails, setResumeDetails] = useState({});
  const [resumeId, setResumeId] = useState('');
  const [searchName, setSearchName] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleUploadResume = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/uploadResumeDetails', {
        name: 'John Doe',
        currentJobTitle: 'Software Engineer',
        currentJobDescription: 'Developing web applications',
        currentJobCompany: 'Tech Company Inc',
      });

      setResumeId(response.data.resumeId);
    } catch (error) {
      console.error('Error uploading resume:', error.response.data);
    }
  };

  const handleGetResumeById = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/getResumeById/${resumeId}`);
      setResumeDetails(response.data);
    } catch (error) {
      console.error('Error getting resume by ID:', error.response.data);
    }
  };

  const handleGetResumeByName = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/getResumeByName/${searchName}`);
      if (response && response.data) {
        setSearchResults(response.data);
      } else {
        console.error('Invalid response from the server:', response);
      }
    } catch (error) {
      console.error('Error getting resume by name:', error.response?.data || error.message);
    }
  };

  return (
    <div>
      <h2>Resume Operations</h2>

      <button onClick={handleUploadResume}>Upload Resume</button>
      <br />
      {resumeId && (
        <>
          <button onClick={handleGetResumeById}>Get Resume by ID</button>
          <p>Resume ID: {resumeId}</p>
          {Object.keys(resumeDetails).length > 0 && (
            <div>
              <h3>Resume Details</h3>
              <pre>{JSON.stringify(resumeDetails, null, 2)}</pre>
            </div>
          )}
        </>
      )}

      <br />
      <input
        type="text"
        placeholder="Enter name for search"
        value={searchName}
        onChange={(e) => setSearchName(e.target.value)}
      />
      <button onClick={handleGetResumeByName}>Search Resumes by Name</button>
      {searchResults.length > 0 && (
        <div>
          <h3>Search Results</h3>
          {searchResults.map((result) => (
            <div key={result.id}>
              <pre>{JSON.stringify(result, null, 2)}</pre>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ResumeComponent;
