import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [name, setName] = useState('');
  const [jobTitle, setJobTitle] = useState('');
  const [jobDescription, setJobDescription] = useState('');
  const [company, setCompany] = useState('');
  const [resumeId, setResumeId] = useState('');
  const [resumeName, setResumeName] = useState('');
  const [popupMessage, setPopupMessage] = useState('');
  const [resumeDetails, setResumeDetails] = useState(null);

  const handleUpload = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/uploadResumeDetails', {
        name,
        currentJobTitle: jobTitle,
        currentJobDescription: jobDescription,
        currentJobCompany: company,
      });
      setPopupMessage(`Resume uploaded successfully! Resume ID: ${response.data.resumeId}`);
    } catch (error) {
      setPopupMessage('Error uploading resume.');
    }
  };

  const handleGetById = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/getResumeById/${resumeId}`);
      setResumeDetails(response.data);
      setPopupMessage('Resume details retrieved successfully!');
    } catch (error) {
      setPopupMessage('Error retrieving resume details.');
    }
  };

  const handleGetByName = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/getResumeByName', {
        params: {
          name: resumeName,
        },
      });
      console.log('Response from getResumeByName:', response.data);
      setResumeDetails(response.data);
      setPopupMessage('Resume details retrieved successfully!');
    } catch (error) {
      console.error('Error retrieving resume details by name:', error);
      setPopupMessage('Error retrieving resume details.');
    }
  };

  return (
    <div>
      <h1>Resume App</h1>

      <div>
        <h2>UPLOAD RESUME DETAILS:</h2>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <div>
          <label>Job Title:</label>
          <input type="text" value={jobTitle} onChange={(e) => setJobTitle(e.target.value)} />
        </div>
        <div>
          <label>Job Description:</label>
          <input type="text" value={jobDescription} onChange={(e) => setJobDescription(e.target.value)} />
        </div>
        <div>
          <label>Company:</label>
          <input type="text" value={company} onChange={(e) => setCompany(e.target.value)} />
        </div>
        <button onClick={handleUpload}>Upload Resume</button>
      </div>

      <div>
        <h2>SEARCH BY ID:</h2>
        <div>
          <label>ID:</label>
          <input type="text" value={resumeId} onChange={(e) => setResumeId(e.target.value)} />
          <button onClick={handleGetById}>Search</button>
        </div>
      </div>

      <div>
        <h2>SEARCH BY NAME:</h2>
        <div>
          <label>Name:</label>
          <input type="text" value={resumeName} onChange={(e) => setResumeName(e.target.value)} />
          <button onClick={handleGetByName}>Search</button>
        </div>
      </div>

      {popupMessage && (
        <div className="popup">
          <p>{popupMessage}</p>
          <button onClick={closePopup}>Close</button>
        </div>
      )}

      {resumeDetails && (
        <div>
          <h2>Resume Details</h2>
          <pre>{JSON.stringify(resumeDetails, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
