import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [resumeDetails, setResumeDetails] = useState({});
  const [resumeId, setResumeId] = useState('');
  const [resumeName, setResumeName] = useState('');
  const [popupMessage, setPopupMessage] = useState('');

  const handleUpload = async (data) => {
    try {
      const response = await axios.post('http://localhost:8080/api/uploadResumeDetails', data);
      setPopupMessage(`Resume uploaded successfully! Resume ID: ${response.data.resumeId}`);
    } catch (error) {
      setPopupMessage('Error uploading resume.');
    }
  };

  const handleGetById = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/getResumeById/${resumeId}`);
      setResumeDetails(response.data);
      setPopupMessage('Resume details retrieved successfully!');
    } catch (error) {
      setPopupMessage('Error retrieving resume details.');
    }
  };

  const handleGetByName = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/getResumeByName/${encodeURIComponent(resumeName)}`);
      setResumeDetails(response.data);
      setPopupMessage('Resume details retrieved successfully!');
    } catch (error) {
      setPopupMessage('Error retrieving resume details.');
    }
  };

  return (
    <div>
      <h1>Resume App</h1>
      <div>
        <h2>Upload Resume</h2>
        <button onClick={() => handleUpload({ name: 'John Doe', currentJobTitle: 'Developer', currentJobDescription: 'Web Developer', currentJobCompany: 'Tech Company' })}>
          Upload Resume
        </button>
      </div>

      <div>
        <h2>Get Resume by ID</h2>
        <input type="text" placeholder="Enter Resume ID" value={resumeId} onChange={(e) => setResumeId(e.target.value)} />
        <button onClick={handleGetById}>Get Resume</button>
      </div>

      <div>
        <h2>Get Resume by Name</h2>
        <input type="text" placeholder="Enter Full Name" value={resumeName} onChange={(e) => setResumeName(e.target.value)} />
        <button onClick={handleGetByName}>Get Resume</button>
      </div>

      {popupMessage && (
        <div className="popup">
          <p>{popupMessage}</p>
          <button onClick={() => setPopupMessage('')}>Close</button>
        </div>
      )}

      {Object.keys(resumeDetails).length > 0 && (
        <div>
          <h2>Resume Details</h2>
          <pre>{JSON.stringify(resumeDetails, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
